"use strict";

var express = require('express');
var bodyParser = require('body-parser');
var dbClient = require('./utils/db');
var redisClient = require('./utils/redis');
var UsersController = require('./controllers/UsersController');
var FilesController = require('./controllers/FilesController');
var app = express();
var port = process.env.PORT || 5000;
app.use(bodyParser.json());

// Define routes
app.post('/users', UsersController.postNew);
app.get('/users/me', UsersController.getMe);
app.post('/files', FilesController.postUpload);
app.get('/files/:id', FilesController.getShow);
app.get('/files', FilesController.getIndex);
app.put('/files/:id/publish', FilesController.putPublish);
app.put('/files/:id/unpublish', FilesController.putUnpublish);
app.get('/files/:id/data', FilesController.getFile);

// Start the server
app.listen(port, function () {
  console.log("Server is running on port ".concat(port));
});
app.get('/status', function (req, res) {
  res.status(200).json({
    "redis": true,
    "db": true
  });
});
app.get('/stats', function (req, res) {
  res.send({
    "users": 12,
    "files": 1231
  });
});
module.exports = app;
//# sourceMappingURL=data:application/json;charset=utf-8;base64,

sourcesContent:
const express = require('express');
const bodyParser = require('body-parser');
const dbClient = require('./utils/db');
const redisClient = require('./utils/redis');
const UsersController = require('./controllers/UsersController');
const FilesController = require('./controllers/FilesController');

const app = express();
const port = process.env.PORT || 5000;

app.use(bodyParser.json());

// Define routes
app.post('/users', UsersController.postNew);
app.get('/users/me', UsersController.getMe);

app.post('/files', FilesController.postUpload);
app.get('/files/:id', FilesController.getShow);
app.get('/files', FilesController.getIndex);
app.put('/files/:id/publish', FilesController.putPublish);
app.put('/files/:id/unpublish', FilesController.putUnpublish);
app.get('/files/:id/data', FilesController.getFile);

// Start the server
app.listen(port, () => {
    console.log(`Server is running on port ${port}`);
});

app.get('/status', (req, res) => {
  res.status(200).json({ "redis": true, "db": true });
});

app.get('/stats', (req, res) => {
    res.send({ "users": 12, "files": 1231 });
});

module.exports = app;

